document.addEventListener('DOMContentLoaded', () => {
  try {
    const instructionSection = document.querySelector('#instruction-section');
    const parabolaDemo = document.querySelector('#parabola-demo');
    const equationDisplay = document.querySelector('#equation-display');

    let a = 1, b = 0, c = 0;
    let isDragging = false;
    let dragStartX = 0;
    let dragStartA = 0;
    let dragStartB = 0;

    const p5Instance = new p5((p) => {
      p.setup = () => {
        p.createCanvas(600, 400).parent(parabolaDemo);
        p.noLoop();
      };

      p.draw = () => {
        p.background(255);
        p.stroke(0);
        p.strokeWeight(2);
        p.translate(p.width / 2, p.height / 2);
        p.scale(1, -1);

        for (let x = -p.width / 2; x <= p.width / 2; x++) {
          const y = a * (x * x) / (p.width * p.width) + b * x / p.width + c;
          p.line(x, y, x + 1, a * ((x + 1) * (x + 1)) / (p.width * p.width) + b * (x + 1) / p.width + c);
        }

        p.fill(255, 0, 0);
        p.ellipse(0, 0, 10, 10);
      };
    });

    parabolaDemo.addEventListener('mousedown', (event) => {
      isDragging = true;
      dragStartX = event.clientX;
      dragStartA = a;
      dragStartB = b;
    });

    parabolaDemo.addEventListener('mousemove', (event) => {
      if (isDragging) {
        const dx = event.clientX - dragStartX;
        a = dragStartA + dx * 0.01;
        b = dragStartB + dx * 0.01;
        p5Instance.redraw();
        updateEquation();
      }
    });

    parabolaDemo.addEventListener('mouseup', () => {
      isDragging = false;
    });

    parabolaDemo.addEventListener('mouseleave', () => {
      isDragging = false;
    });

    function updateEquation() {
      equationDisplay.innerHTML = `y = ${a.toFixed(2)}x^2 + ${b.toFixed(2)}x + ${c.toFixed(2)}`;
      MathJax.typeset();
    }

    updateEquation();

    instructionSection.innerHTML = '操作说明：拖拽抛物线以观察其变化。';
  } catch (error) {
    console.error('Error initializing interactive elements:', error);
  }
});

